 *
 * TIP: Consider using this library along with {SlotDerivation}.
 */
library StorageSlot {
    struct AddressSlot {
        address value;
    }

    struct BooleanSlot {
        bool value;
    }

    struct Bytes32Slot {
        bytes32 value;
    }

    struct Uint256Slot {
        uint256 value;
    }

    struct Int256Slot {
        int256 value;
    }

    struct StringSlot {
        string value;
    }

    struct BytesSlot {
        bytes value;
    }

    /**
     * @dev Returns an `AddressSlot` with member `value` located at `slot`.
     */
    function getAddressSlot(bytes32 slot) internal pure returns (AddressSlot storage r) {
        assembly ("memory-safe") {
            r.slot := slot
        }
    }

    /**
     * @dev Returns a `BooleanSlot` with member `value` located at `slot`.
     */
    function getBooleanSlot(bytes32 slot) internal pure returns (BooleanSlot storage r) {
        assembly ("memory-safe") {
            r.slot := slot
        }
    }

    /**
     * @dev Returns a `Bytes32Slot` with member `value` located at `slot`.
     */
    function getBytes32Slot(bytes32 slot) internal pure returns (Bytes32Slot storage r) {
        assembly ("memory-safe") {
            r.slot := slot
        }
    }

    /**
     * @dev Returns a `Uint256Slot` with member `value` located at `slot`.
     */
    function getUint256Slot(bytes32 slot) internal pure returns (Uint256Slot storage r) {
        assembly ("memory-safe") {
            r.slot := slot
        }
    }

    /**
     * @dev Returns a `Int256Slot` with member `value` located at `slot`.
     */
    function getInt256Slot(bytes32 slot) internal pure returns (Int256Slot storage r) {
        assembly ("memory-safe") {
            r.slot := slot
        }
    }

    /**
     * @dev Returns a `StringSlot` with member `value` located at `slot`.
     */
    function getStringSlot(bytes32 slot) internal pure returns (StringSlot storage r) {
        assembly ("memory-safe") {
            r.slot := slot
        }
    }

    /**
     * @dev Returns an `StringSlot` representation of the string storage pointer `store`.
     */
    function getStringSlot(string storage store) internal pure returns (StringSlot storage r) {
        assembly ("memory-safe") {
            r.slot := store.slot
        }
    }

    /**
     * @dev Returns a `BytesSlot` with member `value` located at `slot`.
     */
    function getBytesSlot(bytes32 slot) internal pure returns (BytesSlot storage r) {
        assembly ("memory-safe") {
            r.slot := slot
        }
    }

    /**
     * @dev Returns an `BytesSlot` representation of the bytes storage pointer `store`.
     */
    function getBytesSlot(bytes storage store) internal pure returns (BytesSlot storage r) {
        assembly ("memory-safe") {
            r.slot := store.slot
        }
    }
}

contracts/LiquidityPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title LiquidityPool
 * @dev Contrato para gerenciamento de pools de liquidez
 */
contract LiquidityPool is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Eventos
    event LiquidityAdded(
        address indexed provider,
        uint256 amountA,
        uint256 amountB,
        uint256 liquidity
    );
    event LiquidityRemoved(
        address indexed provider,
        uint256 amountA,
        uint256 amountB,
        uint256 liquidity
    );
    event Swap(
        address indexed user,
        uint256 amountIn,
        uint256 amountOut,
        address tokenIn,
        address tokenOut
    );

    // Estruturas
    struct Pool {
        IERC20 tokenA;
        IERC20 tokenB;
        uint256 reserveA;
        uint256 reserveB;
        uint256 totalSupply;
        uint256 fee; // em base 10000 (0.3% = 30)
    }

    // Estado
    Pool public pool;
    mapping(address => uint256) public balances;
    uint256 private constant MINIMUM_LIQUIDITY = 1000;

    // Modificadores
    modifier enoughLiquidity(uint256 amount) {
        require(amount > 0, "Insufficient liquidity");
        _;
    }

    /**
     * @dev Construtor
     * @param _tokenA Endereço do token A
     * @param _tokenB Endereço do token B
     * @param _fee Taxa da pool (base 10000)
     */
    constructor(
        address _tokenA,
        address _tokenB,
        uint256 _fee
    ) Ownable(msg.sender) {
        require(_tokenA != address(0) && _tokenB != address(0), "Invalid tokens");
        require(_fee <= 1000, "Fee too high"); // Max 10%

        pool.tokenA = IERC20(_tokenA);
        pool.tokenB = IERC20(_tokenB);
        pool.fee = _fee;
    }

    /**
     * @dev Adiciona liquidez à pool
     * @param amountADesired Quantidade desejada do token A
     * @param amountBDesired Quantidade desejada do token B
     * @param amountAMin Quantidade mínima do token A
     * @param amountBMin Quantidade mínima do token B
     */
    function addLiquidity(
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin
    ) external nonReentrant whenNotPaused returns (uint256 amountA, uint256 amountB, uint256 liquidity) {
        require(amountADesired >= amountAMin && amountBDesired >= amountBMin, "Insufficient desired amount");
        require(amountADesired > 0 && amountBDesired > 0, "Invalid amounts");

        // Verificar saldos dos usuários
        require(pool.tokenA.balanceOf(msg.sender) >= amountADesired, "Insufficient tokenA balance");
        require(pool.tokenB.balanceOf(msg.sender) >= amountBDesired, "Insufficient tokenB balance");

        // Verificar allowances
        require(pool.tokenA.allowance(msg.sender, address(this)) >= amountADesired, "Insufficient tokenA allowance");
        require(pool.tokenB.allowance(msg.sender, address(this)) >= amountBDesired, "Insufficient tokenB allowance");

        if (pool.totalSupply == 0) {
            amountA = amountADesired;
            amountB = amountBDesired;
            liquidity = sqrt(amountA * amountB) - MINIMUM_LIQUIDITY;
            _mint(address(0), MINIMUM_LIQUIDITY);
        } else {
            uint256 amountBOptimal = quote(amountADesired, pool.reserveA, pool.reserveB);
            if (amountBOptimal <= amountBDesired) {
                require(amountBOptimal >= amountBMin, "Insufficient B amount");
                amountA = amountADesired;
                amountB = amountBOptimal;
            } else {
                uint256 amountAOptimal = quote(amountBDesired, pool.reserveB, pool.reserveA);
                require(amountAOptimal <= amountADesired, "Excessive A amount");
                require(amountAOptimal >= amountAMin, "Insufficient A amount");
                amountA = amountAOptimal;
                amountB = amountBDesired;
            }
            liquidity = min(
                (amountA * pool.totalSupply) / pool.reserveA,
                (amountB * pool.totalSupply) / pool.reserveB
            );
        }

        require(liquidity > 0, "Insufficient liquidity minted");

        pool.tokenA.safeTransferFrom(msg.sender, address(this), amountA);
        pool.tokenB.safeTransferFrom(msg.sender, address(this), amountB);

        pool.reserveA += amountA;
        pool.reserveB += amountB;
        _mint(msg.sender, liquidity);

        emit LiquidityAdded(msg.sender, amountA, amountB, liquidity);
    }

    /**
     * @dev Remove liquidez da pool
     * @param liquidity Quantidade de tokens LP a queimar
     * @param amountAMin Quantidade mínima do token A
     * @param amountBMin Quantidade mínima do token B
     */
    function removeLiquidity(
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin
    ) external nonReentrant whenNotPaused enoughLiquidity(liquidity) returns (uint256 amountA, uint256 amountB) {
        require(balances[msg.sender] >= liquidity, "Insufficient balance");
        require(liquidity > 0, "Invalid liquidity amount");
        require(amountAMin > 0 && amountBMin > 0, "Invalid minimum amounts");

        amountA = (liquidity * pool.reserveA) / pool.totalSupply;
        amountB = (liquidity * pool.reserveB) / pool.totalSupply;

        require(amountA >= amountAMin && amountB >= amountBMin, "Insufficient amounts");

        _burn(msg.sender, liquidity);
        pool.tokenA.safeTransfer(msg.sender, amountA);
        pool.tokenB.safeTransfer(msg.sender, amountB);

        pool.reserveA -= amountA;
        pool.reserveB -= amountB;

        emit LiquidityRemoved(msg.sender, amountA, amountB, liquidity);
    }

    /**
     * @dev Executa swap de tokens
     * @param amountIn Quantidade de entrada
     * @param amountOutMin Quantidade mínima de saída
     * @param tokenIn Token de entrada
     * @param tokenOut Token de saída
     */
    function swap(
        uint256 amountIn,
        uint256 amountOutMin,
        address tokenIn,
        address tokenOut
    ) external nonReentrant whenNotPaused returns (uint256 amountOut) {
        require(
            tokenIn == address(pool.tokenA) || tokenIn == address(pool.tokenB),
            "Invalid input token"
        );
        require(
            tokenOut == address(pool.tokenA) || tokenOut == address(pool.tokenB),
            "Invalid output token"
        );
        require(tokenIn != tokenOut, "Same tokens");
        require(amountIn > 0, "Invalid input amount");
        require(amountOutMin > 0, "Invalid minimum output");

        // Verificar saldo do usuário
        require(IERC20(tokenIn).balanceOf(msg.sender) >= amountIn, "Insufficient balance");

        // Verificar allowance
        require(IERC20(tokenIn).allowance(msg.sender, address(this)) >= amountIn, "Insufficient allowance");

        bool isTokenA = tokenIn == address(pool.tokenA);
        (uint256 reserveIn, uint256 reserveOut) = isTokenA
            ? (pool.reserveA, pool.reserveB)
            : (pool.reserveB, pool.reserveA);

        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        uint256 amountInWithFee = amountIn * (10000 - pool.fee);
        amountOut = (amountInWithFee * reserveOut) / ((reserveIn * 10000) + amountInWithFee);

        require(amountOut >= amountOutMin, "Insufficient output amount");

        if (isTokenA) {
            pool.reserveA += amountIn;
            pool.reserveB -= amountOut;
            pool.tokenB.safeTransfer(msg.sender, amountOut);
        } else {
            pool.reserveB += amountIn;
            pool.reserveA -= amountOut;
            pool.tokenA.safeTransfer(msg.sender, amountOut);
        }

        emit Swap(msg.sender, amountIn, amountOut, tokenIn, tokenOut);
    }

    /**
     * @dev Calcula preço baseado nas reservas
     */
    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) public pure returns (uint256 amountB) {
        require(amountA > 0, "Insufficient amount");
        require(reserveA > 0 && reserveB > 0, "Insufficient liquidity");
        amountB = (amountA * reserveB) / reserveA;
    }

    /**
     * @dev Calcula raiz quadrada (otimizado para gas)
     */
    function sqrt(uint256 y) internal pure returns (uint256 z) {
        if (y > 3) {
            z = y;
            uint256 x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }

    /**
     * @dev Retorna o menor valor
     */
    function min(uint256 x, uint256 y) internal pure returns (uint256) {
        return x < y ? x : y;
    }

    /**
     * @dev Mint de tokens LP
     */
    function _mint(address to, uint256 value) private {
        pool.totalSupply += value;
        balances[to] += value;
    }

    /**
     * @dev Burn de tokens LP
     */
    function _burn(address from, uint256 value) private {
        balances[from] -= value;
        pool.totalSupply -= value;
    }

    /**
     * @dev Pausa o contrato
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Despausa o contrato
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/SmartWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title SmartWallet
 * @dev Contrato principal da carteira inteligente
 */
contract SmartWallet is Ownable, ReentrancyGuard, Pausable {
    // Eventos
    event WalletCreated(address indexed owner, address wallet);
    event TokensTransferred(address indexed from, address indexed to, address token, uint256 amount);
    event NativeTokenTransferred(address indexed from, address indexed to, uint256 amount);
    event GuardianAdded(address indexed wallet, address indexed guardian);
    event GuardianRemoved(address indexed wallet, address indexed guardian);
    event LimitsUpdated(address indexed wallet, uint256 dailyLimit, uint256 monthlyLimit);

    // Estruturas
    struct WalletInfo {
        bool exists;
        uint256 dailyLimit;
        uint256 monthlyLimit;
        uint256 dailySpent;
        uint256 monthlySpent;
        uint256 lastDayReset;
        uint256 lastMonthReset;
        mapping(address => bool) guardians;
        uint256 guardiansCount;
    }

    // Estado
    mapping(address => WalletInfo) public wallets;
    mapping(address => bool) public whitelistedTokens;
    mapping(bytes32 => bool) public usedSignatures; // Para prevenir replay attacks
    uint256 public constant MAX_GUARDIANS = 3;
    uint256 public constant RATE_LIMIT_PERIOD = 1 minutes; // Rate limiting
    uint256 public constant MAX_TRANSACTIONS_PER_PERIOD = 10;

    // Modificadores
    modifier onlyWalletOwner(address wallet) {
        require(wallets[wallet].exists, "Wallet does not exist");
        require(msg.sender == wallet, "Not wallet owner");
        _;
    }

    modifier withinLimits(address wallet, uint256 amount) {
        require(checkLimits(wallet, amount), "Transaction exceeds limits");
        _;
    }

    modifier rateLimited(address wallet) {
        require(checkRateLimit(wallet), "Rate limit exceeded");
        _;
    }

    modifier notContract() {
        require(msg.sender == tx.origin, "Contracts not allowed");
        _;
    }

    modifier validAddress(address addr) {
        require(addr != address(0), "Invalid address");
        require(addr != address(this), "Cannot use contract address");
        _;
    }

    /**
     * @dev Construtor
     */
    constructor() Ownable(msg.sender) {
        _pause(); // Inicialmente pausado para configuração
    }

    /**
     * @dev Cria uma nova carteira com verificações de segurança
     * @param initialLimit Limite diário inicial
     */
    function createWallet(uint256 initialLimit) external notContract {
        require(!wallets[msg.sender].exists, "Wallet already exists");
        require(initialLimit > 0, "Invalid initial limit");

        WalletInfo storage wallet = wallets[msg.sender];
        wallet.exists = true;
        wallet.dailyLimit = initialLimit;
        wallet.monthlyLimit = initialLimit * 30;
        wallet.lastDayReset = block.timestamp;
        wallet.lastMonthReset = block.timestamp;

        emit WalletCreated(msg.sender, msg.sender);
    }

    /**
     * @dev Transfere tokens ERC20 com verificações de segurança
     * @param token Endereço do token
     * @param to Destinatário
     * @param amount Quantidade
     */
    function transferTokens(
        address token,
        address to,
        uint256 amount
    ) external nonReentrant whenNotPaused onlyWalletOwner(msg.sender) withinLimits(msg.sender, amount) rateLimited(msg.sender) validAddress(to) {
        require(whitelistedTokens[token], "Token not whitelisted");
        require(to != address(0), "Invalid recipient");
        require(to != address(this), "Cannot transfer to contract");
        require(amount > 0, "Invalid amount");

        // Verificar saldo do usuário
        uint256 userBalance = IERC20(token).balanceOf(msg.sender);
        require(userBalance >= amount, "Insufficient balance");

        // Verificar allowance
        uint256 allowance = IERC20(token).allowance(msg.sender, address(this));
        require(allowance >= amount, "Insufficient allowance");

        // Executar transferência
        IERC20(token).transferFrom(msg.sender, to, amount);
        updateSpentLimits(msg.sender, amount);
        updateRateLimit(msg.sender);

        emit TokensTransferred(msg.sender, to, token, amount);
    }

    /**
     * @dev Transfere tokens nativos (ETH/MATIC/BNB) com verificações de segurança
     * @param to Destinatário
     */
    function transferNative(
        address payable to
    ) external payable nonReentrant whenNotPaused onlyWalletOwner(msg.sender) withinLimits(msg.sender, msg.value) rateLimited(msg.sender) validAddress(to) {
        require(to != address(0), "Invalid recipient");
        require(to != address(this), "Cannot transfer to contract");
        require(msg.value > 0, "Invalid amount");
        require(address(this).balance >= msg.value, "Insufficient contract balance");

        // Executar transferência com verificação de sucesso
        (bool success, ) = to.call{value: msg.value}("");
        require(success, "Transfer failed");

        updateSpentLimits(msg.sender, msg.value);
        updateRateLimit(msg.sender);

        emit NativeTokenTransferred(msg.sender, to, msg.value);
    }

    /**
     * @dev Adiciona um guardião
     * @param guardian Endereço do guardião
     */
    function addGuardian(address guardian) external onlyWalletOwner(msg.sender) {
        require(guardian != address(0), "Invalid guardian");
        require(!wallets[msg.sender].guardians[guardian], "Guardian already exists");
        require(wallets[msg.sender].guardiansCount < MAX_GUARDIANS, "Max guardians reached");

        wallets[msg.sender].guardians[guardian] = true;
        wallets[msg.sender].guardiansCount++;

        emit GuardianAdded(msg.sender, guardian);
    }

    /**
     * @dev Remove um guardião
     * @param guardian Endereço do guardião
     */
    function removeGuardian(address guardian) external onlyWalletOwner(msg.sender) {
        require(wallets[msg.sender].guardians[guardian], "Guardian does not exist");

        wallets[msg.sender].guardians[guardian] = false;
        wallets[msg.sender].guardiansCount--;

        emit GuardianRemoved(msg.sender, guardian);
    }

    /**
     * @dev Atualiza limites da carteira
     * @param wallet Endereço da carteira
     * @param newDailyLimit Novo limite diário
     * @param newMonthlyLimit Novo limite mensal
     */
    function updateLimits(
        address wallet,
        uint256 newDailyLimit,
        uint256 newMonthlyLimit
    ) external onlyOwner {
        require(wallets[wallet].exists, "Wallet does not exist");
        require(newMonthlyLimit >= newDailyLimit * 30, "Invalid monthly limit");

        wallets[wallet].dailyLimit = newDailyLimit;
        wallets[wallet].monthlyLimit = newMonthlyLimit;

        emit LimitsUpdated(wallet, newDailyLimit, newMonthlyLimit);
    }

    /**
     * @dev Verifica se uma transação está dentro dos limites
     * @param wallet Endereço da carteira
     * @param amount Valor da transação
     */
    function checkLimits(address wallet, uint256 amount) internal returns (bool) {
        WalletInfo storage info = wallets[wallet];

        // Reset diário
        if (block.timestamp >= info.lastDayReset + 1 days) {
            info.dailySpent = 0;
            info.lastDayReset = block.timestamp;
        }

        // Reset mensal
        if (block.timestamp >= info.lastMonthReset + 30 days) {
            info.monthlySpent = 0;
            info.lastMonthReset = block.timestamp;
        }

        return (
            info.dailySpent + amount <= info.dailyLimit &&
            info.monthlySpent + amount <= info.monthlyLimit
        );
    }

    /**
     * @dev Atualiza os gastos após uma transação
     * @param wallet Endereço da carteira
     * @param amount Valor da transação
     */
    function updateSpentLimits(address wallet, uint256 amount) internal {
        WalletInfo storage info = wallets[wallet];
        info.dailySpent += amount;
        info.monthlySpent += amount;
    }

    /**
     * @dev Verifica rate limiting
     * @param wallet Endereço da carteira
     */
    function checkRateLimit(address wallet) internal view returns (bool) {
        WalletInfo storage info = wallets[wallet];

        if (block.timestamp >= info.lastDayReset + RATE_LIMIT_PERIOD) {
            return true; // Reset do período
        }

        return info.guardiansCount < MAX_TRANSACTIONS_PER_PERIOD;
    }

    /**
     * @dev Atualiza rate limiting após uma transação
     * @param wallet Endereço da carteira
     */
    function updateRateLimit(address wallet) internal {
        WalletInfo storage info = wallets[wallet];

        if (block.timestamp >= info.lastDayReset + RATE_LIMIT_PERIOD) {
            info.guardiansCount = 1;
            info.lastDayReset = block.timestamp;
        } else {
            info.guardiansCount++;
        }
    }

    /**
     * @dev Adiciona token à whitelist
     * @param token Endereço do token
     */
    function whitelistToken(address token) external onlyOwner {
        require(token != address(0), "Invalid token");
        whitelistedTokens[token] = true;
    }

    /**
     * @dev Remove token da whitelist
     * @param token Endereço do token
     */
    function removeTokenFromWhitelist(address token) external onlyOwner {
        require(token != address(0), "Invalid token");
        whitelistedTokens[token] = false;
    }

    /**
     * @dev Pausa o contrato
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Despausa o contrato
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/SmartWalletFixed.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title SmartWalletFixed
 * @dev Contrato principal da carteira inteligente com correções de segurança
 */
contract SmartWalletFixed is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Eventos
    event WalletCreated(address indexed owner, address wallet);
    event TokensTransferred(address indexed from, address indexed to, address token, uint256 amount);
    event NativeTokenTransferred(address indexed from, address indexed to, uint256 amount);
    event GuardianAdded(address indexed wallet, address indexed guardian);
    event GuardianRemoved(address indexed wallet, address indexed guardian);
    event LimitsUpdated(address indexed wallet, uint256 dailyLimit, uint256 monthlyLimit);
    event SecurityEvent(string indexed eventType, address indexed user, string details);

    // Estruturas
    struct WalletInfo {
        bool exists;
        uint256 dailyLimit;
        uint256 monthlyLimit;
        uint256 dailySpent;
        uint256 monthlySpent;
        uint256 lastDayReset;
        uint256 lastMonthReset;
        mapping(address => bool) guardians;
        uint256 guardiansCount;
        uint256 nonce; // Para prevenir replay attacks
        uint256 lastTransactionTime; // Para rate limiting
        uint256 transactionCount; // Para rate limiting
    }

    // Estado
    mapping(address => WalletInfo) public wallets;
    mapping(address => bool) public whitelistedTokens;
    mapping(bytes32 => bool) public usedSignatures; // Para prevenir replay attacks
    uint256 public constant MAX_GUARDIANS = 3;
    uint256 public constant RATE_LIMIT_PERIOD = 1 minutes; // Rate limiting
    uint256 public constant MAX_TRANSACTIONS_PER_PERIOD = 10;

    // Modificadores
    modifier onlyWalletOwner(address wallet) {
        require(wallets[wallet].exists, "Wallet does not exist");
        require(msg.sender == wallet, "Not wallet owner");
        _;
    }

    modifier withinLimits(address wallet, uint256 amount) {
        require(checkLimits(wallet, amount), "Transaction exceeds limits");
        _;
    }

    modifier rateLimited(address wallet) {
        require(checkRateLimit(wallet), "Rate limit exceeded");
        _;
    }

    modifier notContract() {
        require(msg.sender == tx.origin, "Contracts not allowed");
        _;
    }

    modifier validAddress(address addr) {
        require(addr != address(0), "Invalid address");
        require(addr != address(this), "Cannot use contract address");
        _;
    }

    /**
     * @dev Construtor
     */
    constructor() Ownable(msg.sender) {
        _pause(); // Inicialmente pausado para configuração
    }

    /**
     * @dev Cria uma nova carteira
     * @param initialLimit Limite diário inicial
     */
    function createWallet(uint256 initialLimit) external notContract {
        require(!wallets[msg.sender].exists, "Wallet already exists");
        require(initialLimit > 0, "Invalid initial limit");

        WalletInfo storage wallet = wallets[msg.sender];
        wallet.exists = true;
        wallet.dailyLimit = initialLimit;
        wallet.monthlyLimit = initialLimit * 30;
        wallet.lastDayReset = block.timestamp;
        wallet.lastMonthReset = block.timestamp;
        wallet.nonce = 0;
        wallet.lastTransactionTime = 0;
        wallet.transactionCount = 0;

        emit WalletCreated(msg.sender, msg.sender);
        emit SecurityEvent("WALLET_CREATED", msg.sender, "New wallet created");
    }

    /**
     * @dev Transfere tokens ERC20 com verificações de segurança
     * @param token Endereço do token
     * @param to Destinatário
     * @param amount Quantidade
     */
    function transferTokens(
        address token,
        address to,
        uint256 amount
    ) external 
        nonReentrant 
        whenNotPaused 
        onlyWalletOwner(msg.sender) 
        withinLimits(msg.sender, amount)
        rateLimited(msg.sender)
        validAddress(to)
    {
        require(whitelistedTokens[token], "Token not whitelisted");
        require(amount > 0, "Invalid amount");

        // Verificar saldo do usuário
        uint256 userBalance = IERC20(token).balanceOf(msg.sender);
        require(userBalance >= amount, "Insufficient balance");

        // Verificar allowance
        uint256 allowance = IERC20(token).allowance(msg.sender, address(this));
        require(allowance >= amount, "Insufficient allowance");

        // Executar transferência usando SafeERC20
        IERC20(token).safeTransferFrom(msg.sender, to, amount);

        // Atualizar limites e rate limiting
        updateSpentLimits(msg.sender, amount);
        updateRateLimit(msg.sender);

        emit TokensTransferred(msg.sender, to, token, amount);
        emit SecurityEvent("TOKEN_TRANSFER", msg.sender, "Token transfer executed");
    }

    /**
     * @dev Transfere tokens nativos (ETH/MATIC/BNB) com verificações de segurança
     * @param to Destinatário
     */
    function transferNative(
        address payable to
    ) external 
        payable 
        nonReentrant 
        whenNotPaused 
        onlyWalletOwner(msg.sender) 
        withinLimits(msg.sender, msg.value)
        rateLimited(msg.sender)
        validAddress(to)
    {
        require(msg.value > 0, "Invalid amount");
        require(address(this).balance >= msg.value, "Insufficient contract balance");

        // Executar transferência
        (bool success, ) = to.call{value: msg.value}("");
        require(success, "Transfer failed");

        // Atualizar limites e rate limiting
        updateSpentLimits(msg.sender, msg.value);
        updateRateLimit(msg.sender);

        emit NativeTokenTransferred(msg.sender, to, msg.value);
        emit SecurityEvent("NATIVE_TRANSFER", msg.sender, "Native token transfer executed");
    }

    /**
     * @dev Adiciona um guardião
     * @param guardian Endereço do guardião
     */
    function addGuardian(address guardian) external onlyWalletOwner(msg.sender) validAddress(guardian) {
        require(guardian != msg.sender, "Cannot add self as guardian");
        require(!wallets[msg.sender].guardians[guardian], "Guardian already exists");
        require(wallets[msg.sender].guardiansCount < MAX_GUARDIANS, "Max guardians reached");

        wallets[msg.sender].guardians[guardian] = true;
        wallets[msg.sender].guardiansCount++;

        emit GuardianAdded(msg.sender, guardian);
        emit SecurityEvent("GUARDIAN_ADDED", msg.sender, "Guardian added");
    }

    /**
     * @dev Remove um guardião
     * @param guardian Endereço do guardião
     */
    function removeGuardian(address guardian) external onlyWalletOwner(msg.sender) {
        require(wallets[msg.sender].guardians[guardian], "Guardian does not exist");

        wallets[msg.sender].guardians[guardian] = false;
        wallets[msg.sender].guardiansCount--;

        emit GuardianRemoved(msg.sender, guardian);
        emit SecurityEvent("GUARDIAN_REMOVED", msg.sender, "Guardian removed");
    }

    /**
     * @dev Atualiza limites da carteira
     * @param wallet Endereço da carteira
     * @param newDailyLimit Novo limite diário
     * @param newMonthlyLimit Novo limite mensal
     */
    function updateLimits(
        address wallet,
        uint256 newDailyLimit,
        uint256 newMonthlyLimit
    ) external onlyOwner {
        require(wallets[wallet].exists, "Wallet does not exist");
        require(newDailyLimit > 0, "Invalid daily limit");
        require(newMonthlyLimit >= newDailyLimit * 30, "Invalid monthly limit");

        wallets[wallet].dailyLimit = newDailyLimit;
        wallets[wallet].monthlyLimit = newMonthlyLimit;

        emit LimitsUpdated(wallet, newDailyLimit, newMonthlyLimit);
        emit SecurityEvent("LIMITS_UPDATED", wallet, "Wallet limits updated");
    }

    /**
     * @dev Verifica se uma transação está dentro dos limites
     * @param wallet Endereço da carteira
     * @param amount Valor da transação
     */
    function checkLimits(address wallet, uint256 amount) internal returns (bool) {
        WalletInfo storage info = wallets[wallet];

        // Reset diário
        if (block.timestamp >= info.lastDayReset + 1 days) {
            info.dailySpent = 0;
            info.lastDayReset = block.timestamp;
        }

        // Reset mensal
        if (block.timestamp >= info.lastMonthReset + 30 days) {
            info.monthlySpent = 0;
            info.lastMonthReset = block.timestamp;
        }

        return (
            info.dailySpent + amount <= info.dailyLimit &&
            info.monthlySpent + amount <= info.monthlyLimit
        );
    }

    /**
     * @dev Verifica rate limiting
     * @param wallet Endereço da carteira
     */
    function checkRateLimit(address wallet) internal view returns (bool) {
        WalletInfo storage info = wallets[wallet];

        if (block.timestamp >= info.lastTransactionTime + RATE_LIMIT_PERIOD) {
            return true; // Reset do período
        }

        return info.transactionCount < MAX_TRANSACTIONS_PER_PERIOD;
    }

    /**
     * @dev Atualiza os gastos após uma transação
     * @param wallet Endereço da carteira
     * @param amount Valor da transação
     */
    function updateSpentLimits(address wallet, uint256 amount) internal {
        WalletInfo storage info = wallets[wallet];
        info.dailySpent += amount;
        info.monthlySpent += amount;
    }

    /**
     * @dev Atualiza rate limiting após uma transação
     * @param wallet Endereço da carteira
     */
    function updateRateLimit(address wallet) internal {
        WalletInfo storage info = wallets[wallet];

        if (block.timestamp >= info.lastTransactionTime + RATE_LIMIT_PERIOD) {
            info.transactionCount = 1;
            info.lastTransactionTime = block.timestamp;
        } else {
            info.transactionCount++;
        }
    }

    /**
     * @dev Adiciona token à whitelist
     * @param token Endereço do token
     */
    function whitelistToken(address token) external onlyOwner validAddress(token) {
        whitelistedTokens[token] = true;
        emit SecurityEvent("TOKEN_WHITELISTED", token, "Token added to whitelist");
    }

    /**
     * @dev Remove token da whitelist
     * @param token Endereço do token
     */
    function removeTokenFromWhitelist(address token) external onlyOwner {
        require(token != address(0), "Invalid token");
        whitelistedTokens[token] = false;
        emit SecurityEvent("TOKEN_BLACKLISTED", token, "Token removed from whitelist");
    }

    /**
     * @dev Pausa o contrato
     */
    function pause() external onlyOwner {
        _pause();
        emit SecurityEvent("CONTRACT_PAUSED", msg.sender, "Contract paused");
    }

    /**
     * @dev Despausa o contrato
     */
    function unpause() external onlyOwner {
        _unpause();
        emit SecurityEvent("CONTRACT_UNPAUSED", msg.sender, "Contract unpaused");
    }

    /**
     * @dev Função para receber ETH
     */
    receive() external payable {
        emit SecurityEvent("ETH_RECEIVED", msg.sender, "ETH received by contract");
    }

    /**
     * @dev Função de emergência para recuperar ETH
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No ETH to withdraw");

        (bool success, ) = owner().call{value: balance}("");
        require(success, "Withdrawal failed");

        emit SecurityEvent("EMERGENCY_WITHDRAW", msg.sender, "Emergency withdrawal executed");
    }
}

